mapper.update:
UPDATE PRODUCT 
SET PRODUCTDEL =#{del}
WHERE PRODUCTNO =#{productNo}

mapper.select:
UPDATE DETAIL 
SET DETAILDIV = #{type}
WHERE DETAILNO =#{detailNo}

UPDATE DETAIL 
SET MOUNT = #{mount}
WHERE DETAILNO =#{detailNo}

mapper.update:
UPDATE PRORDER
SET ORDERCK = #{type}
WHERE ORDERNO = #{orderNo}

UPDATE PRORDER
SET ORDERCK = #{type},ORDERCKDATE = SYSDATE  		
WHERE ORDERNO = #{orderNo}

UPDATE DETAIL
SET MOUNT=#{mount}
WHERE DETAILNO = #{detailNo}

UPDATE DETAIL
SET DETAILDIV =#{del}
WHERE DETAILNO=#{detailNo}
